// src/DocumentList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'; // Import Link component

const DocumentList = () => {
  const [documents, setDocuments] = useState([]);

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const response = await axios.get('http://localhost:3001/');
        setDocuments(response.data);
      } catch (error) {
        console.error('There was an error fetching the documents!', error);
      }
    };

    fetchDocuments();
  }, []);

  const handleDeleteAll = async () => {
    try {
      await axios.delete('http://localhost:3001/deleteAll');
      setDocuments([]); // Clear the document list after deletion
      alert('All documents have been deleted.');
    } catch (error) {
      console.error('There was an error deleting the documents!', error);
      alert('There was an error deleting the documents!');
    }
  };

  return (
    <div>
      <h2>Document List</h2>
      <ul>
        {documents.map((doc) => (
          <li key={doc._id}>
            <h3>{doc.title || 'Untitled'}</h3>
            <p>{doc.content || 'No content'}</p>
            <small>{new Date(doc.created_at).toLocaleString()}</small>
          </li>
        ))}
      </ul>
      <Link to="/add" className="add-document-link">
        Add New Document
      </Link>
      <button onClick={handleDeleteAll} className="delete-all-button">
        Delete All Documents
      </button>
    </div>
  );
};

export default DocumentList;
