// src/DocumentList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const DocumentList = () => {
  const [documents, setDocuments] = useState([]);
  const navigate = useNavigate(); // Use navigate for redirection

  useEffect(() => {
    const fetchDocuments = async () => {
      try {
        const response = await axios.get('http://localhost:3001/');
        setDocuments(response.data);
      } catch (error) {
        console.error('There was an error fetching the documents!', error);
      }
    };

    fetchDocuments();
  }, []);

  const handleNavigateToAdd = () => {
    navigate('/add'); // Navigate to the add document page
  };

  const handleDeleteAll = async () => {
    try {
      await axios.delete('http://localhost:3001/deleteAll');
      setDocuments([]); // Clear the document list after deletion
      navigate('/'); // Redirect to the home page or refresh the page
    } catch (error) {
      console.error('There was an error deleting the documents!', error);
      alert('There was an error deleting the documents!');
    }
  };

  const handleNavigateToUpdate = (id) => {
    navigate(`/update/${id}`); // Navigate to the update page with the document id
  };

  return (
    <div>
      <h2>Document List</h2>
      <ul>
        {documents.map((doc) => (
          <li key={doc._id}>
            <h3>
              <button 
                onClick={() => handleNavigateToUpdate(doc._id)} 
                className="document-title-button"
              >
                {doc.title || 'Untitled'}
              </button>
            </h3>
            <p>{doc.content || 'No content'}</p>
            <small>{new Date(doc.created_at).toLocaleString()}</small>
          </li>
        ))}
      </ul>
      <button onClick={handleNavigateToAdd} className="add-document-button">
        Add New Document
      </button>
      <br />
      <button onClick={handleDeleteAll} className="delete-all-button">
        Delete All Documents
      </button>
    </div>
  );
};

export default DocumentList;
